                    <script>
                        function deleteGalley()
                        {
                            const selectedInputs = document.querySelectorAll('input[name="img-select[]"]:checked');
                            const selectedIds = Array.from(selectedInputs).map(input => input.id.replace('img-select', ''));

                            if (selectedIds.length === 0) {
                                alert('لطفاً حداقل یک تصویر را انتخاب کنید.');
                                return;
                            }

                            fetch("{{ route('hotel.deleteGallery', $sharedData->id) }}", {
                                method: 'POST',
                                headers: {
                                    'Content-Type': 'application/json',
                                    'X-CSRF-TOKEN': '{{ csrf_token() }}'
                                },
                                body: JSON.stringify({ ids: selectedIds })
                            })
                                .then(response => response.json())
                                .then(data => {
                                    if (data.success) {
                                        alert('تصاویر با موفقیت حذف شدند.');
                                        location.reload();
                                    } else {
                                        alert('خطا در حذف تصاویر.');
                                    }
                                })
                                .catch(error => {
                                    console.error('Error:', error);
                                    alert('خطا در ارتباط با سرور.');
                                });
                        }
                    </script>
